import {BaseStore} from 'frill-core';
import jwtDecode from 'jwt-decode';

/**
 * Authentication store
 * @extends {FrillCore.BaseStore}
 * @example <caption>via frill component</caption>
 * this.getFrill().store('Auth').jwt // get JWT
 * @example <caption>via frill action</caption>
 * this.dispatch('AUTH_LOGIN', token); // login with JWT
 */
class AuthStore extends BaseStore {

  /**
   * Constructor
   */
  constructor() {
    super();
    this._user = null;
    this._jwt = null;

    /**
     * Actions to listen
     * @param {Object} actions
     * @listens {AUTH_LOGIN}
     * @listens {AUTH_LOGOUT}
     */
    this.actions = {
      'AUTH_LOGIN': 'login',
      'AUTH_LOGOUT': 'logout',
    };
  }

  /**
   * Login using JWT
   * @param {string} token - a valid JsonWebToken
   * @emits {change}
   * @see http://jwt.io/
   */
  login(token) {
    // store these to localStorage
    this._jwt = token;
    this._user = jwtDecode(this._jwt);
    this.change();
  }

  /**
   * Logout
   * @emits {change}
   */
  logout() {
    // remove these from localStorage
    this._jwt = null;
    this._user = null;
    this.change();
  }

  /**
   * User data
   * @type {Object}
   */
  get user() {
    return this._user;
  }

  /**
   * JsonWebToken
   * @type {string}
   */
  get jwt() {
    return this._jwt;
  }

  /**
   * Check if logged in
   * @returns {boolean}
   */
  isLoggedIn() {
    return Boolean(this._user);
  }
}


/**
 * Export AuthStore instance
 */
export default new AuthStore();

import {BaseStore} from 'frill-core';

/**
 * Example store
 * @extends {FrillCore.BaseStore}
 * @example <caption>via frill component</caption>
 * this.getFrill().store('Example').getCount(); // get count
 * @example <caption>via frill action</caption>
 * this.dispatch('COUNT_UP_BY', 15); // count up counter by 15
 */
class ExampleStore extends BaseStore {

  /**
   * Constructor
   */
  constructor() {
    super();
    this._count = 0;
    this._scrollItems = [];
    this._scrollItemsCount = 0;
    this._scrollItemTotal = 0;

    /**
     * Actions to listen
     * @param {Object} actions
     * @listens {COUNT_UP}
     * @listens {COUNT_UP_BY}
     * @listens {LOAD_SCROLL_ITEMS}
     */
    this.actions = {
      'COUNT_UP': 'countup',
      'COUNT_UP_BY': 'countupBy',
      'LOAD_SCROLL_ITEMS': 'loadScrollItems',
    };
  }

  /**
   * Count up by 1
   * @emits {change}
   */
  countup() {
    this._count = this._count + 1;
    this.change();
  }

  /**
   * Count up by a number given
   * @param {number} byCount - a number to count up by
   * @emits {change}
   */
  countupBy(byCount) {
    this._count = this._count + byCount;
    this.change();
  }

  /**
   * Gets current count
   * @type {number}
   */
  get count() {
    return this._count;
  }

  /**
   * Load scroll items
   * @param {Object} payload.data - data of items
   * @param {Object} payload.total - total count of items
   * @emits {change}
   */
  loadScrollItems({data, total}) {
    this._scrollItems = this._scrollItems.concat(data);
    this._scrollItemsCount = this._scrollItemsCount + data.length;
    this._scrollItemTotal = total;
    this.change();
  }

  /**
   * Gets items
   * @type {Object}
   */
  get scrollItems() {
    return this._scrollItems;
  }

  /**
   * Gets loaded items count
   * @type {number}
   */
  get scrollItemsCount() {
    return this._scrollItemsCount;
  }

  /**
   * Gets total count of items
   * @return {number} - get number of total items
   */
  get scrollItemTotal() {
    return this._scrollItemTotal;
  }
}

