import hapiAuthJwt from 'hapi-auth-jwt2';
import {where as _where} from 'lodash';

// Test data
import accounts from '../../../fixtures/testUserData';

const validator = (decoded, request, callback) => {
  /**
   * construct search query
   * @todo: search from session
   */
  const query = {
    id: parseInt(decoded.user.id, 10),
  };
  const user = _where(accounts, query);
  if (user.length === 0) {
    return callback(null, false);
  } else {
    return callback(null, true);
  }
};

/**
 * Authentication strategy for JsonWebTokens(JWT)
 * @see http://jwt.io/
 */
const jwtStrategy = (server) => {
  server.register(hapiAuthJwt, (err) => {
    if (err) server.log(['error'], 'jwt-validator load error');

    server.auth.strategy('jwt', 'jwt', true, {
      key: 'NeverShareYourSecret',
      // key: 'VQkdGKTWhA4FJHWYXkps7q8q8SNQGvdFjVob2hlEVjQ8AijsWAOdPxkThiDQsDg',
      validateFunc: validator,
      verifyOptions: { algorithms: ['HS256'] },
    });
  });
};

export default jwtStrategy;

import Boom from 'boom';
import {where as _where} from 'lodash';

// Test data
import accounts from '../../../fixtures/testUserData';

const authenticate = (request, reply) => {
  const credentials = {};
  reply.continue({ credentials });
};

const payload = (request, reply) => {
  // construct search query
  if (!request.payload) {
    return reply(Boom.badRequest('invalid username or password'));
  }

  const query = {
    name: request.payload.username,
    password: request.payload.password,
  };

  let user = _where(accounts, query);

  if (user.length === 0) {
    return reply(Boom.badRequest('invalid username or password'));
  }

  user = user[0];

  request.auth.credentials = {
    provider: 'local',
    token: null,
    secret: null,
    query: {},
    profile: {
      id: user.id,
      username: user.name,
      displayName: user.displayName,
      raw: user, // full profile
    },
  };

  reply.continue();
};

const response = (request, reply) => {
  reply.continue();
};

const scheme = () => {
  return {
    payload,
    authenticate,
    response,
    options: {
      payload: true,
    },
  };
};

/**
 * Authentication strategy for local authentications
 */
const localStrategy = (server) => {
  server.auth.scheme('local', scheme);
  server.auth.strategy('local', 'local');
};

export default localStrategy;

