import Joi from 'joi';
import JWT from 'jsonwebtoken';
import Boom from 'boom';
import aguid from 'aguid';
import {where as _where} from 'lodash';
import {clone as _clone} from 'lodash';

// Test data
import accounts from '../../fixtures/testUserData';

/**
 * Local authentication routes.
 */
const localAuthRoutes = [{
  method: ['POST'],
  path: '/login',
  config: {
    validate: {
      query: {
        ref: Joi.string().optional(),
      },
      payload: {
        username: Joi.string().min(5).required(),
        password: Joi.string().min(5).required(),
      },
    },
    tags: ['api', 'auth'],
    auth: 'local',
    // auth: 'twitter',
    plugins: {
      'hapi-swagger': {
        responseMessages: [
          { code: 400, message: 'Bad Request' },
          { code: 500, message: 'Internal Server Error'},
        ],
      },
    },
  },
  handler: (request, reply) => {
    // reply error when authentication fails
    if (!request.auth.isAuthenticated) {
      return reply(
        Boom.unauthorized('Authentication failed due to: ' +
          request.auth.error.message)
      );
    }

    const query = {
      id: parseInt(request.auth.credentials.profile.id, 10),
      strategy: request.auth.strategy,
    };

    const user = _where(accounts, query);
    // console.log(user);

    if (user.length === 0) {
      console.log('needs registration');
    }

    const _user = _clone(user[0]);
    delete _user.password;

    /** @todo create and set JWT into session */
    const session = {
      valid: true,
      id: aguid(), // a random session id
      user: _user,
      exp: new Date().getTime() + 30 * 60 * 1000, // expires in 30 minutes time
    };

    // // create the session in Redis
    // redisClient.set(session.id, JSON.stringify(session));
    request.session.set(session.id, session);
    // sign the session as a JWT
    const token = JWT.sign(session, 'NeverShareYourSecret');
    return reply({token: token});
  },
}];

export default localAuthRoutes;

import Joi from 'joi';
import Holiday from '../../helpers/Holiday';

/**
 * REST API - posts
 */
const posts = (server, Models) => {
  const {mysql} = Models;

  const PostApi = new Holiday('sequelize', 'posts', mysql.Posts);

  PostApi.validate.save({
    title: Joi.string().required(),
    content: Joi.string().required(),
  });

  PostApi.validate.update({
    title: Joi.string(),
    content: Joi.string(),
  });

  return PostApi.all();
};

export default posts;

import frillCore from 'frill-core';
import Stores from './stores';
import Actions from './actions';

/**
 * FrillCore
 */
const FrillCore = frillCore;

// load stores and actions.
FrillCore._Stores = Stores;
FrillCore._Actions = Actions;

export default FrillCore;

import Frill from './bootstrap';
import _extend from 'lodash/object/extend';
import React from 'react';
import Router from 'react-router';
import routerContainer from './helpers/routerContainer';
import _routes from './routes';
const routes = _routes();

// create a frill context
const frillContext = Frill.attach(Frill._Stores, Frill._Actions);

const data = JSON.parse(
  document.getElementById('initial-data').getAttribute('data-json')
);

const router = Router.create({
  routes: routes,
  location: Router.HistoryLocation,
});

routerContainer.set(router);

router.run((Handler) => {
  React.render(
    React.createElement(
      Handler, _extend({frill: frillContext}, data)
    ), document.getElementById('app')
  );
});

import React from 'react';
import {StoreWatchComponent} from 'frill-core';
import {Link, RouteHandler} from 'react-router';

/**
 * App component
 * @extends {FrillCore.StoreWatchComponent}
 * @example <caption>Usage in React-Router</caption>
 * <Route name="app" path="/" handler={AppComponent} />
 */
class AppComponent extends new StoreWatchComponent(['Auth']) {

  /**
   * Constructor
   * @param {any} props
   */
  constructor(props) {
    super(props);

    /**
     * Name of component
     */
    this.name = 'App';
  }

  getStateFromFrill() {
    return {
      isLoggedIn: this.getFrill().store('Auth').isLoggedIn(),
      user: this.getFrill().store('Auth').user,
    };
  }

  /**
   * render
   * @return {React DOM}
   * @see https://facebook.github.io/react/docs/component-specs.html#render
   */
  render() {
    const isLoggedIn = this.state.isLoggedIn;
    return (
      <div className="App">
        <header>
          <h1><img src="/images/frill-logo-white.png" />Welcome to frill!</h1>
        </header>
        <nav>
          <ul>
            <li><Link to="top">Top page</Link></li>
            <li><Link to="login">Login Sample</Link></li>
            <li>
              <a href="http://localhost:3000/auth/twitter/login">
                Twitter Auth Test
              </a>
            </li>
          </ul>
        </nav>
        {(isLoggedIn ? () => {
          return (
            <p className="welcome">Hello, {this.state.user.user.displayName}</p>
          );
        } : () => {
          return (
            <p className="welcome">Welcome, Guest</p>
          );
        })()}
        <RouteHandler {...this.props}/>
        <footer>
          <p>frill</p>
        </footer>
      </div>
    );
  }
}

/**
 * Export AppComponent
 */
export default AppComponent;

import React from 'react';
import {BaseComponent} from 'frill-core';

/**
 * Error component - handles error with status code
 * @extends {FrillCore.BaseComponent}
 * @example <caption>Usage in React-Router</caption>
 * <NotFoundRoute name="Error" handler={C.Error}/>
 */
class ErrorComponent extends BaseComponent {
  /**
   * Constructor
   * @param {any} props
   */
  constructor(props) {
    super(props);

    /**
     * Name of component
     */
    this.name = 'Error';
  }

  /**
   * render
   * @return {React DOM}
   * @see https://facebook.github.io/react/docs/component-specs.html#render
   */
  render() {
    const status = this.props.status;
    return (
      <div>
        <a href="/">Back to top</a>
        <div>CODE: {status.statusCode}</div>
        <div>ERROR: {status.error}</div>
        <div>MESSAGE: {status.message}</div>
      </div>
    );
  }
}

/**
 * Exports ErrorComponent
 */
export default ErrorComponent;

import React from 'react';
import {BaseComponent} from 'frill-core';

/**
 * Login component
 * @extends {FrillCore.BaseComponent}
 * @example <caption>Usage in React-Router</caption>
 * <Route name="login" path="/login" handler={LoginComponent} />
 */
class LoginComponent extends BaseComponent {
  /**
   * Constructor
   * @param {any} props
   */
  constructor(props) {
    super(props);

    this._bind([
      'onClick',
    ]);
  }

  /**
   * Click handler
   */
  onClick(e) {
    e.preventDefault();
    const username = React.findDOMNode(this.refs.username).value;
    const password = React.findDOMNode(this.refs.password).value;
    this.getFrill().action('Auth').login(username, password);
  }

  /**
   * render
   * @return {React DOM}
   * @see https://facebook.github.io/react/docs/component-specs.html#render
   */
  render() {
    return (
      <div className="Login">
        <h2>Login Sample</h2>
        <form>
          <p className="form-item">
            <input type="text" ref="username" placeholder="username" />
          </p>
          <p className="form-item">
            <input type="password" ref="password" placeholder="password" />
          </p>
          <p className="form-item">
            <button onClick={this.onClick}>login</button>
          </p>
        </form>
      </div>
    );
  }
}

/**
 * Export LoginComponent
 */
export default LoginComponent;

import React from 'react';
import {StoreWatchComponent} from 'frill-core';
import {canUseDOM} from 'react/lib/ExecutionEnvironment';
import {RouteHandler} from 'react-router';
// import jwtDecode from 'jwt-decode';

/**
 * Page component - wraps up all pages
 * @extends {FrillCore.StoreWatchComponent}
 * @example <caption>Usage in React-Router</caption>
 * <Route handler={C.Page}>
 *   ...
 * </Route>
 */
class PageComponent extends new StoreWatchComponent(['Auth']) {

  /**
   * Constructor
   */
  constructor(props) {
    super(props);

    /**
     * Name of component
     */
    this.name = 'App';

    if (this.props && this.props.token) {
      this.getFrill().action('Auth').authenticate(this.props.token);
      this.state = this.getInitialStateFromFrill();
    }

    if (canUseDOM) {
      // clear up initial-data from dom
      document.getElementById('initial-data').outerHTML = '';
    }
  }

  getInitialStateFromFrill() {
    super.getInitialStateFromFrill();
  }

  getStateFromFrill() {
    return {
      isLoggedIn: this.getFrill().store('Auth').isLoggedIn(),
    };
  }

  /**
   * render
   * @return {React DOM}
   * @see https://facebook.github.io/react/docs/component-specs.html#render
   */
  render() {
    return (
      <RouteHandler {...this.props}/>
    );
  }
}

/**
 * Exports PageComponent
 */
export default PageComponent;

import React from 'react';
import {canUseDOM} from 'react/lib/ExecutionEnvironment';

/**
 * ScrollBlock component for infinite scrolling
 * @extends {React.Component}
 * @example <caption>Usage in React component</caption>
 * import ScrollBlock from 'ScrollBlock';
 * ...
 * render() {
 *   return (
 *     <ScrollBlock
 *       fetchData={this.onLoadScrollItems}
 *       itemsCount={this.state.scrollItemsCount}
 *       itemTotal={this.state.scrollItemTotal}>
 *       <ul className="scroll-list">
 *         {items}
 *       </ul>
 *     </ScrollBlock>
 *   );
 * }
 */
class ScrollBlockComponent extends React.Component {
  /**
   * Constructor
   * @param {any} props
   */
  constructor(props) {
    super(props);

    /**
     * Name of component
     */
    this.name = 'ScrollBlock';

    /**
     * State of component
     */
    this.state = {
      isLoading: false,
      // NOTE: changed from 'isAllLoaded'
      isLoadedAll: false,
    };

    this.fetchData = this.fetchData.bind(this);
    this.onScroll = this.onScroll.bind(this);
  }

  /**
   * componentDidMount
   * @see https://facebook.github.io/react/docs/component-specs.html#mounting-componentdidmount
   */
  componentDidMount() {
    if (canUseDOM) {
      React.findDOMNode(this.refs.scrollBlock)
        .addEventListener('scroll', this.onScroll);
    }

    if (!this.props.itemsCount) {
      this.fetchData();
    }
  }

  /**
   * componentWillReceiveProps
   * @see https://facebook.github.io/react/docs/component-specs.html#updating-componentwillreceiveprops
   */
  componentWillReceiveProps(newProps) {
    if (newProps.children) {
      this.setState({
        isLoading: false,
      });
    }
  }

  /**
   * componentWillUnmount
   * @see https://facebook.github.io/react/docs/component-specs.html#unmounting-componentwillunmount
   */
  componentWillUnmount() {
    React.findDOMNode(this.refs.scrollBlock)
      .removeEventListener('scroll', this.onScroll);
  }

  /**
   * Scroll handler
   */
  onScroll() {
    if (!this.state.isLoading && !this.state.isLoadedAll) {
      const _elem = React.findDOMNode(this.refs.scrollBlock);
      const _scrolled = Math.round(_elem.scrollHeight - _elem.scrollTop);

      if (_scrolled - _elem.clientHeight >= -1
        && _scrolled - _elem.clientHeight <= 1) {
        // if items are all loaded
        if (this.props.itemTotal &&
          (this.props.itemTotal === this.props.itemsCount)) {
          this.setState({
            isLoading: true,
            isLoadedAll: true,
          });

        // else if there are more items to load
        } else {
          this.fetchData();
        }
      }
    }
  }

  /**
   * render
   * @return {React DOM}
   * @see https://facebook.github.io/react/docs/component-specs.html#render
   */
  render() {
    const _className = this.state.isLoading ? ' is-loading' : '';
    const isLoadedAll = this.state.isLoadedAll;

    return (
      <div className="ScrollBlock" ref="scrollBlock">
        {this.props.children}
        <p className={`ScrollBlock-loading${_className}`}>
          {(!isLoadedAll ? () => {
            // return 'loading...' when there are more items to load
            return 'loading ...';
          } : () => {
            // when all items loaded
            return 'loaded!';
          })()}
        </p>
      </div>
    );
  }

  /**
   * load the data from the parent's function
   */
  fetchData() {
    this.setState({
      isLoading: true,
    });
    this.props.fetchData();
  }
}

/**
 * PropTypes
 */
ScrollBlockComponent.propTypes = {
  // function to fetch new items
  fetchData: React.PropTypes.func,
  // Count of loaded items
  itemsCount: React.PropTypes.number,
  // Count of total items
  itemTotal: React.PropTypes.number,
};

/**
 * Export ScrollBlockComponent
 */
export default ScrollBlockComponent;

import React from 'react';
import {StoreWatchComponent} from 'frill-core';
import ScrollBlock from '../ScrollBlock';

/**
 * Top component
 * @extends {FrillCore.StoreWatchComponent}
 * @example <caption>Usage in React-Router</caption>
 * <Route name="login" path="/login" handler={LoginComponent} />
 */
class TopComponent extends new StoreWatchComponent(['Example']) {

  /**
   * Constructor
   * @param {any} props
   */
  constructor(props) {
    super(props);

    this._bind([
      'onOne',
      'onTen',
      'onFetchItems',
    ]);
  }

  /**
   * Count up by one
   */
  onOne() {
    this.getFrill().action('Example').countUp();
  }

  /**
   * Count up by ten
   */
  onTen() {
    this.getFrill().action('Example').countUpBy(10);
  }

  /**
   * Fetch items
   */
  onFetchItems() {
    this.getFrill()
      .action('Example')
      .loadScrollItems(this.state.scrollItemsCount, 5);
  }

  /**
   * getStateFromFrill
   * @listens {ExampleStore}
   */
  getStateFromFrill() {
    return {
      count: this.getFrill().store('Example').count,
      scrollItems: this.getFrill().store('Example').scrollItems,
      scrollItemsCount: this.getFrill().store('Example').scrollItemsCount,
      scrollItemTotal: this.getFrill().store('Example').scrollItemTotal,
    };
  }

  /**
   * render
   * @return {React DOM}
   * @see https://facebook.github.io/react/docs/component-specs.html#render
   */
  render() {
    const items = [];

    this.state.scrollItems.map((item) => {
      items.push(
        <li key={item.id}>
          <p>ID : {item.id}</p>
          <p>title : {item.title}</p>
        </li>
      );
    });

    return (
      <div className="Top">
        <h2>Examples</h2>
        <section>
          <h3>Button - counter</h3>
          <p className="count">{this.state.count}</p>
          <button onClick={this.onOne}>+1</button>
          <button onClick={this.onTen}>+10</button>
        </section>
        <section>
          <h3>Textarea</h3>
          <textarea placeholder="type something ..."></textarea>
        </section>
        <section>
          <h3>Infinite Scroll</h3>
          <ScrollBlock
            fetchData={this.onFetchItems}
            itemsCount={this.state.scrollItemsCount}
            itemTotal={this.state.scrollItemTotal}>
              <h4>Scroll down the box ...</h4>
              <ul className="scroll-list">
                {items}
              </ul>
          </ScrollBlock>
        </section>
      </div>
    );
  }
}

